module.exports = function(parameterMap){
    this.clientToken = parameterMap.token;
    this.taksistId = parameterMap.taksistId;
    this.cost = parameterMap.cost;
    this.doAction = function(){
        var userHandler = require('userHandler');
        var client = userHandler.clientHandler.getUserByToken(this.clientToken);
        require('orderHandler').generateOrder(
            client.getIssue().data.id,
            client.data.id,
            this.taksistId,
            this.cost
        );
    }
};
