module.exports = function(parameterMap){
    this.action = parameterMap.action;
    this.order = parameterMap.order;
    this.client = parameterMap.client;
    this.doAction = function() {
        var message = require('contextHandler').NewMessage(this.action,{distance: this.order.distance});
        this.client.send(message);
    }
};
