function NewErrorMessageBody(errorCode){
    return {error: errorCode};
}
function NewMessageBody(action, body){
    var res;
    if (body === undefined){
        res = {action: action};
    }
    else{
        res = body;
        res.action = action;
    }
    return res;
}
function NewMessage(action, body){
    var res = NewMessageBody(action, body);
    return JSON.stringify(res);
}
function NewErrorMessage(action, errorCode){
    var errorBody = NewErrorMessageBody(errorCode);
    return NewMessage(action, errorBody);
}

module.exports.NewErrorMessageBody = NewErrorMessageBody;
module.exports.NewErrorMessage = NewErrorMessage;
module.exports.NewMessageBody = NewMessageBody;
module.exports.NewMessage = NewMessage;